const $=e=>document.querySelector(e);var countHasPassed=!1;const countdown=function(e){const t=$(e.target).getAttribute("data-date").split("-"),r=parseInt(t[0]),n=parseInt(t[1]),a=parseInt(t[2]);let o,d,s=$(e.target).getAttribute("data-time");null!=s&&(s=s.split(":"),o=parseInt(s[0]),d=parseInt(s[1]));(new Date).getFullYear();let i=new Date;i.getDate(),i.getMonth(),i.getFullYear(),i.getHours(),i.getMinutes();const g=new Date(a,n-1,r,o,d,0,0).getTime();$(e.target+" .day .word").innerHTML=e.dayWord,$(e.target+" .hour .word").innerHTML=e.hourWord,$(e.target+" .min .word").innerHTML=e.minWord,$(e.target+" .sec .word").innerHTML=e.secWord;const u=()=>{const t=(new Date).getTime(),r=g-t;if(r<0)countHasPassed=!0,$(e.target).innerHTML="";else{const t=Math.floor(r/864e5),n=Math.floor(r%864e5/36e5),a=Math.floor(r%36e5/6e4),o=Math.floor(r%6e4/1e3);requestAnimationFrame(u),$(e.target+" .day .num").innerHTML=addZero(t),$(e.target+" .hour .num").innerHTML=addZero(n),$(e.target+" .min .num").innerHTML=addZero(a),$(e.target+" .sec .num").innerHTML=addZero(o)}};u()},addZero=e=>e<10&&e>=0?"0"+e:e;